import React, {useState, useEffect} from 'react';
import { AiFillEye, AiFillGithub } from 'react-icons/ai';
import { motion } from 'framer-motion';
//import { AppWrap } from '../../'
//import { urlFor, client} from '../../'
import './Work.scss';
import images from '../../constants/images';

const Work = () => {
  // states
  const [activeFilter, setActiveFilter] = useState('All');
  const [animateCard, setAnimateCard] = useState({ y: 0, opacity: 1});
  const [works, setWorks] = useState([
    { name: 'front-end', imgUrl: images.about01, title: 'Website', description: 'lorem ipsum', tag: 'Websites' },
    { name: 'front-end', imgUrl: images.about02, title: 'Website', description: 'lorem ipsum', tag: 'Websites' },
    { name: 'Corporate Design', imgUrl: images.about03, title: 'Corporate Design', description: 'lorem ipsum', tag: 'UX/UI' },
    { name: 'Photography', imgUrl: images.about04, title: 'Photoshoot', description: 'lorem ipsum', tag: 'Photo' }
  ]);
  const [filterWork, setFilterWork] = useState([]);

  const handleWorkFilter = (tag) => {
    setActiveFilter(tag);

    if (tag === 'All') {
      setFilterWork(works);
    } else {
      const filteredWorks = works.filter(work => work.tag === tag);
      setFilterWork(filteredWorks);
    }
  }


  useEffect(() => {
    // Set the initial filtered works to all works
    setFilterWork(works);
  }, [works]);

  return (
    <> 
      <h2 className='head-text'>
        Our creative 
        <span> Portfolio </span>
        Section
      </h2>
      <div className='app__work-filter'>
        {['UI/UX', 'Websites', 'Logos', 'Video', 'Photo'].map((item, index) => (
          <div key={index}
          onClick={() => handleWorkFilter(item)}
          className={`app__work-filter-item app__flex p-text ${activeFilter === item ? 'item-active' : ''}`}
          >
            {item}
          </div>
        ))}
      </div>
      <motion.div 
      animate={animateCard}
      transition={{ duration: 0.5, delayChildren: 0.5 }}
      className="app__work-portfolio">
        {filterWork.map((work, index) => (
          <div className='app__work-item app__flex' key={index}>
            <div className='app__work-img app__flex'>
              <img src={work.imgUrl} alt={work.name}/>
              <motion.div
                whileHover={{ opacity: [0, 1]}}
                transition={{ duration: 0.25, ease: 'easeInOut', delayChildren: 0.5, staggerChildren: 0.5}}
                className='app__work-hover app__flex'
              >
                <a href={work.projectLink} target="_blank" rel="noreferrer">
                <motion.div
                    whileInView={{ scale: [0, 1] }}
                    whileHover={{ scale: [1, 0.90] }}
                    transition={{ duration: 0.25 }}
                    className="app__flex"
                  >
                    <AiFillEye />
                  </motion.div>
                </a>
                <a href={work.codeLink} target="_blank" rel="noreferrer">
                  <motion.div
                    whileInView={{ scale: [0, 1] }}
                    whileHover={{ scale: [1, 0.90] }}
                    transition={{ duration: 0.25 }}
                    className="app__flex"
                  >
                    <AiFillGithub />
                  </motion.div>
                </a>
              </motion.div>
            </div>
            <div className="app__work-content app__flex">
              <h4 className="bold-text">{work.title}</h4>
              <p className="p-text" style={{ marginTop: 10 }}>{work.description}</p>

              <div className="app__work-tag app__flex">
                {/* <p className="p-text">{work.tags[0]}</p> */}
              </div>
            </div>
          </div>
        ))}
      </motion.div>
    </>
  )
}

export default Work
